mapper.select:
select
  id, otype, onumber, uid, oprice, paystate, otime, ostate
from order

    
        and id = #{id}
    
    
        and otype = #{otype}
    
    
        and onumber = #{onumber}
    
    
        and uid = #{uid}
    
    
        and oprice = #{oprice}
    
    
        and paystate = #{paystate}
    
    
        and otime = #{otime}
    
    
        and ostate = #{ostate}
    

limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from order

    
        and id = #{id}
    
    
        and otype = #{otype}
    
    
        and onumber = #{onumber}
    
    
        and uid = #{uid}
    
    
        and oprice = #{oprice}
    
    
        and paystate = #{paystate}
    
    
        and otime = #{otime}
    
    
        and ostate = #{ostate}
